import React from "react";
import classNames from "./utils/class-names";

const ProgressBar = ({ durationProgress }) => {
	return (
		<div className="row mb-2">
			<div className="col">
				<div className="progress" style={{ height: "20px" }}>
					<div
						className="progress-bar"
						role="progressbar"
						aria-valuemin="0"
						aria-valuemax="100"
						aria-valuenow={durationProgress} // TODO: Increase aria-valuenow as elapsed time increases
						style={{ width: `${durationProgress}%` }} // TODO: Increase width % as elapsed time increases.  Width is dependent on durationProgress
					/>
				</div>
			</div>
		</div>
	);
};

export default ProgressBar;
